import React, { useState } from "react";
import { secondsToDuration } from "../utils/duration";
import { useEffect } from "react";

const TimeLeft = ({ focusLength }) => {
  const [timeLeft, setTimeLeft] = useState(focusLength);
  const [intervalId, setIntervalId] = useState(null); //null when stopped
  //change timeLeft whenever focusLength changes
  useEffect(() => {
    setTimeLeft(focusLength);
  }, [focusLength]);

  const isStarted = intervalId !== null; //when not null, timer started

  //decrement timeLeft by one second
  const handleStartStopClick = () => {
    if (isStarted) {
      console.log(isStarted);
      clearInterval(intervalId)








    } else {
      //if we are in paused mode
      const newIntervalId = setInterval(() => {
        setTimeLeft((prevTimeLeft) => {
          const newTimeLeft = prevTimeLeft - 1;
          if (newTimeLeft >= 0) {
            return prevTimeLeft - 1;
          } else {
            return prevTimeLeft;
          }
        });
      }, 50);
      setIntervalId(newIntervalId)
    }
  };

  let timeLeftInMinutesAndSeconds = secondsToDuration(timeLeft);

  return (
    <div>
      {/* TODO: Update message below to include time remaining in the current session */}
      <p className="lead" data-testid="session-sub-title">
        {timeLeftInMinutesAndSeconds} remaining
        <button onClick={handleStartStopClick}>Start</button>
      </p>
    </div>
  );
};

export default TimeLeft;
